mapper.update:
update experiment_running
running_status=#{runningStatus}, fk_model_id = #{fkModelId}, is_deleted=#{isDeleted}, fk_experiment_id=#{fkExperimentId}, start_time=#{startTime}, end_time=#{endTime}, run_id=#{runId}, fk_dl_result_table_name=#{fkDlResultTableName}, conversation_id=#{conversationId}
where experiment_running.id=#{id}

mapper.select:
select experiment_running.fk_experiment_id as fkExperimentId
from experiment_running
where experiment_running.id=#{runningId}
