mapper.update:
update template
name = #{name}, type = #{type}, fk_user_id = #{fkUserId}, fk_workflow_id = #{fkWorkflowId}, fk_experiment_id = #{fkExperimentId}, tags = #{tags}, is_deleted = #{isDeleted}, workflow_addr = #{workflowAddr}, param_json_string = #{paramJsonString}, ggeditor_object_string = #{ggeditorObjectString}, template_desc = #{templateDesc}
where id = #{id}

update template
set template.fk_experiment_id=null
where template.fk_experiment_id=#{experimentId}

update template

    fk_experiment_id = null,
     param_json_string = #{paramJsonString}

where id = #{id}

mapper.select:
select template.id as id,
        template.name as name,
        template.type as type,
        template.fk_user_id as fkUserId,
        template.fk_workflow_id as fkWrokflowId,
        template.fk_experiment_id as fkExperimentId,
        template.tags as tags,
        template.is_deleted as isDeleted,
        template.workflow_addr as workflowAddr,
        template.param_json_string as paramJsonString,
        template.ggeditor_object_string as ggeditorObjectString,
        template.template_desc as templateDesc
from template
where template.id=template.id
 and template.type= #{type}
 and template.fk_user_id= #{fkUserId}
 and template.fk_workflow_id= #{fkWorkflowId}
 and template.is_deleted = #{isDeleted}
 and template.name = #{name}
 and template.tags = #{tags}
